mapper.select:
select * from (select rownum rn, e.* from (
select * from content where del = 'n' and sort ='d' order by cno asc) e)
where rn between #{startRow} and #{endRow}

select * from (select rownum rn, e.* from (
select * from content where del = 'n' and sort ='m' order by cno desc) e)
where rn between #{startRow} and #{endRow}

select * from (select rownum rn, e.* from ( select * from content where
genre like '%'||#{item}||'%'
and sort='d' and del = 'n' order by cno desc) e) where rn between #{startRow} and #{endRow}

select * from (select rownum rn, e.* from ( select * from content where
genre like '%'||#{item}||'%'
and sort='m' and del = 'n' order by cno desc) e) where rn between #{startRow} and #{endRow}

select * from (select rownum rn, e.* from (
select * from content order by cno) e)
where rn between #{startRow} and #{endRow}
